  this.setState({ todos: [...this.setState.todos.filter(todo => todo.id !== id) ]});
}
  render () {

      return(
    <div className="App">
      <header />
     <Todos todos={this.state.todos} markComplete={this.markComplete}/>
    </div>
  );
}
}
export default App;
