      {
        id: 1,
        title: 'Check your email',
        completed: false
      },
      {
        id: 2,
        title: 'Do stutern Assignment',
        completed: false
      },
      {
        id: 3,
        title: 'Go to the market',
        completed: false
      }
    ]
  }

  markComplete = () => {
    this.setState ({ todos: this.state.todos.map(todo => {
      if(todo.id === id) {
        todo.completed
      }
    }) })
  }

  render () {

      return(
    <div className="App">
     <Todos todos={this.state.todos} markComplete={this.markComplete}/>
    </div>
  );
}
}
export default App;
